import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import {TreeCanvas} from 'butterfly-dag';
import mockData from './data';
import Node from './node';

import 'butterfly-dag/dist/index.css';
import './iconfont.css';
import './index.less';
class IndentedTree extends Component {
  constructor() {
    super();
  }
  componentDidMount() {
    let root = document.getElementById('dag-canvas');

    this.canvas = new TreeCanvas({
      root: root,
      disLinkable: true, // 可删除连线
      linkable: true,    // 可连线
      draggable: true,   // 可拖动
      zoomable: true,    // 可放大
      moveable: true,    // 可平移
      theme: {
        edge: {
          shapeType: 'Manhattan'
        }
      },
      layout: {
        type: 'indented',
        options: {
          direction: 'LR',
          isHorizontal: true,
          indent: 160,
          getHeight: function getHeight() {
            return 16;
          },
          getWidth: function getWidth() {
            return 100;
          }
        }
      }
    });
    this.canvas.draw(mockData, {}, () => {
      this.canvas.focusCenterWithAnimate();
    });

    let _tmpNum = 100;
    this.canvas.on('events', (data) => {
      if (data.type === 'custom:addSubNode') {
        // addNode，removeNode可以还需要重写下
        this.canvas.addNode({
          id: _tmpNum.toString(),
          text: `我是id为${_tmpNum}的节点`,
          parent: data.data.parent,
          Class: Node,
          condition: 'and',
          desc: '请选择指标',
          endpoints: [{
            id: 'left',
            orientation: [-1, 0],
            pos: [0, 0.5]
          }, {
            id: 'bottom',
            orientation: [0, 1],
            pos: [0.5, 0]
          }],
        });
        this.canvas.addEdge({
          id: this.canvas.edges.length.toString(),
          source: 'bottom',
          target: 'left',
          sourceNode: data.data.parent,
          targetNode: (_tmpNum++).toString(),
          type: 'endpoint'
        });
        // TODO :问题在这里
        try {
          this.canvas.redraw();
        } catch (e) {
          console.log(e);
        }
      }
    });
  }
  render() {
    return (
      <div className='indented-tree-page'>
        <div className="indented-tree-canvas" id="dag-canvas">
        </div>
      </div>
    );
  }
}

ReactDOM.render(<IndentedTree />, document.getElementById('root'));

import {TreeNode} from 'butterfly-dag';
import $ from 'jquery';
import './node.less';
class BaseNode extends TreeNode {
  constructor(opts) {
    super(opts);
    this.addIcon = null;
    this.expandBtn = null;
    this.indexExpandNode = null;
  }
  draw = (opts) => {
    let container = $('<div class="rule-node"></div>')
      .css('top', opts.top + 'px')
      .css('left', opts.left + 'px')
      .attr('id', opts.id);

    let textDom = $(`<span class="rule-text">${opts.options.desc}</span>`);
    this.indexExpandNode = $(`<div class="index-expand-node"></div>`);
    let iconBox = $(`<span class="icon-box"></span>`);

    this.addIcon = $(`<i class="iconfont add-icon" title="添加节点">&#xe6a1;</i>`);
    this.expandBtn = $(`<i class="iconfont expand-icon" title="展开">&#xe786;</i>`);
    iconBox.append(this.expandBtn);

    if (opts.options.condition) {
      let selectDom = $('<select class="rule-select"><option value="and">并且</option><option value="or">或者</option></select>');
      container.append(selectDom);
      textDom.addClass('text-right');
      container.append(this.addIcon);
      this.indexExpandNode.append(iconBox);
      this.indexExpandNode.append(textDom);
      container.append(this.indexExpandNode);
    } else {
      textDom.addClass('text-normal');
      container.append(textDom);
      container.append(this.addIcon);
      container.append(iconBox);
    }

    this._attachEvent();

    return container[0];
  }
  _attachEvent() {
    $(this.expandBtn).on('click', (e) => {
      e.stopPropagation();
      e.preventDefault();
      // TODO: 这里存在问题
      // if (this.collapsed) {
      //   // 可以在这里向后端请求数据,把node穿进去expand里面
      //   this.expand();
      // } else {
      //   this.collapse();
      // }
    });

    $(this.addIcon).on('click', (e) => {
      e.stopPropagation();
      e.preventDefault();
      this.emit('events', {
        type: 'custom:addSubNode',
        data: {
          parent: this.id
        }
      });
    });
  }
}

export default BaseNode;

