import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import mockData from './data.js';
import {TreeCanvas} from 'butterfly-dag';

import 'butterfly-dag/dist/index.css';
import './index.less';

class MindMap extends Component {
  componentDidMount() {
    let root = document.getElementById('dag-canvas');
    this.canvas = new TreeCanvas({
      root: root,
      disLinkable: true, // 可删除连线
      linkable: true,    // 可连线
      draggable: true,   // 可拖动
      zoomable: true,    // 可放大
      moveable: true,    // 可平移
      theme: {
        edge: {
          shapeType: 'AdvancedBezier',
        }
      },
      layout: {
        type: 'mindmap',
        options: {
          direction: 'H',
          getSide(d) {
            return d.data.side || 'right';
          },
          getHeight(d) {
            return 10;
          },
          getWidth(d) {
            return 40;
          },
          getHGap(d) {
            return 50;
          },
          getVGap(d) {
            return 20;
          },
        }
      }
    });
    this.canvas.draw(mockData, {}, () => {
      this.canvas.focusCenterWithAnimate();
    }
    );
  }
  render() {
    return (
      <div className='mind-map'>
        <div className="compactBoxTree-canvas" id="dag-canvas">
        </div>
      </div>
    );
  }
}

ReactDOM.render(<MindMap />, document.getElementById('root'));

@import 'theme.less'; 
.mind-map {
  width: 100%;
  height: 100%;
  text-align: center;
  .compactBoxTree-canvas {
    width: 100%;
    height: 100%;
    .butterfly-wrapper {
      #Root {
        width: 100px;
        background: @butterfly-primary-color;
        border: none;
        border-radius: 5px;
        font-weight: 500;
        height: 28px;
        line-height: 28px;
        padding: 0;
        .title {
          color: @butterfly-overlay-font-color;
        }
      }
      .mind-map-node {
        position: absolute;
        background: @butterfly-theme-color;
        border: @butterfly-box-border;
        border-radius: 10px;
        padding: 4px 8px;
        .title {
          color: @butterfly-normal-font-color;
        }
      }
    }
  }
}

import {Node} from 'butterfly-dag';
import $ from 'jquery';

class BaseNode extends Node {
  constructor(opts) {
    super(opts);
  }

  draw = (opts) => {
    let container = $('<div class="mind-map-node"></div>')
      .css('top', opts.top)
      .css('left', opts.left)
      .attr('id', opts.id);
    let titleDom = $(`<div class="title">${opts.options.title}<div>`);

    container.append(titleDom);

    return container[0];
  }
}

export default BaseNode;

