import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import {Canvas} from 'butterfly-dag';
import mockData from './data.js';

import 'butterfly-dag/dist/index.css';
import './index.less';

class Radial extends Component {
  constructor() {
    super();
  }
  componentDidMount() {
    let root = document.getElementById('dag-canvas');
    this.canvas = new Canvas({
      root: root,
      disLinkable: true, // 可删除连线
      linkable: false,    // 可连线
      draggable: true,   // 可拖动
      zoomable: true,    // 可放大
      moveable: true,    // 可平移
      layout: {
        type: 'radial',
        options: {
          // 布局画布总宽度
          width: 800,
          // 布局画布总长度
          height: 800,
          /** 停止迭代的最大迭代数 */
          maxIteration: 200,
          /** 布局中心 */
          center: [400, 400],
          /** 中心点，默认为数据中第一个点 */
          focusNode: '0',
          /** 每一圈半径 */
          unitRadius: 80,
          /** 默认边长度 */
          linkDistance: 100,
          /** 是否防止重叠 */
          preventOverlap: true,
          /** 节点直径 */
          nodeSize: 20,
          /** 节点间距，防止节点重叠时节点之间的最小距离（两节点边缘最短距离） */
          nodeSpacing: undefined,
          /** 是否必须是严格的 radial 布局，即每一层的节点严格布局在一个环上。preventOverlap 为 true 时生效 */
          strictRadial: true,
          /** 防止重叠步骤的最大迭代次数 */
          maxPreventOverlapIteration: 200,
          link: {
            // 线条的距离
            distance: 10,
            // 线条的粗细
            strength: 1
          },
        },
      },
      theme: {
        edge: {
          shapeType: 'Straight',
          arrow: true,
          arrowPosition: 0.8,
        }
      }
    });
    this.canvas.draw(mockData);
  }
  render() {
    return (
      <div className='radial-page'>
        <div className="radial-canvas" id="dag-canvas">
        </div>
      </div>
    );
  }
}

ReactDOM.render(<Radial />, document.getElementById('root'));

import {Node} from 'butterfly-dag';
import $ from 'jquery';
import './node.less';

class BaseNode extends Node {
  constructor(opts) {
    super(opts);
    this.id = opts.id;
    this.top = opts.x;
    this.left = opts.y;
    this.options = opts;
  }
  draw = (opts) => {
    let container = $('<div class="radial-node"></div>')
      .css('top', this.top + 'px')
      .css('left', this.left + 'px')
      .attr('id', this.id = opts.id);
    container.text(opts.options.id);

    return container[0];
  }
}

export default BaseNode;

