@import 'theme.less'; 
#root {
  height: 100%;
}

#bf-layout .trigger {
  font-size: 18px;
  line-height: 64px;
  padding: 0 24px;
  cursor: pointer;
  transition: color 0.3s;
}

#bf-layout .trigger:hover {
  color: #1890ff;
}

#bf-layout .logo {
  height: 32px;
  background: rgba(255, 255, 255, 0.3);
  margin: 16px;
  color: white;
  text-align: center;
  line-height: 32px;
  font-weight: border;
}

.site-layout .site-layout-background {
  background: #fff;
  overflow: hidden;
}

.node {
  box-shadow: @butterfly-box-shadow;
  background-color: @butterfly-theme-color;
  border-radius:0 0 5px 5px;
  .header {
    padding: 10px;
    width: 250px;
    border-radius: 5px 5px 0 0;
    color: @butterfly-overlay-font-color;
    background-color: @butterfly-primary-color;
    border: @butterfly-box-border;
  }

  .content {
    color:@butterfly-normal-font-color;
    padding: 10px; 
    border: @butterfly-box-border;
    border-top: none;
    border-radius: 0 0 5px 5px;
    .info{
      .ant-input-sm {
        height: 29px;
        background-color: @butterfly-theme-color;
      }
    }
    .history {
      padding: 5px 0;
      font-size: 12px;
      border-top: 1px solid #eee;
      margin-top: 10px;
    }
  }
}

.label {
  width: 38px;
  height: 22px;
  background-color: @butterfly-theme-color;
  box-shadow: @butterfly-box-shadow;
  border-radius: @butterfly-box-radius;
  cursor: pointer;
  z-index: 1;
  position: relative;
  outline: none;
  border: @butterfly-box-border;
  color: @butterfly-normal-font-color;
  text-align: center;
  line-height: 19px;
  font-size: 12px;
}
.label:hover {
  background: @butterfly-primary-color;
  color: @butterfly-overlay-font-color;
}

import React from 'react';
import {Tooltip} from 'antd';

const Label = () => {
  return (
    <Tooltip title="我, 是一个label">
      <div className="label">
        +
      </div>
    </Tooltip>
  );
};

export default Label;

import React from 'react';
import {Input} from 'antd';
import PropTypes from 'prop-types';

import './index.less';

const Node = (props) => {
  const {size} = props;

  return (
    <div className="node">
      <div
        className="header"
      >
        {props.title}
      </div>
      {
        size !== 'small' && (
          <div className="content">
            <div className="info">
              <Input
                size="small"
                placeholder="申请人姓名"
              />
            </div>
            <div className="history">
            修改: 10 阅读: 20
            </div>
          </div>
        )
      }
    </div>
  );
};

Node.propTypes = {
  title: PropTypes.string,
  color: PropTypes.string,
  size: PropTypes.string,
};

Node.defaultProps = {
  color: 'black'
};

export default Node;

@butterfly-theme-color-base:#fff;
@butterfly-normal-font-color-base:#222;;
@butterfly-overlay-font-color-base:#FFF;
@butterfly-primary-color-base:#F66902;
@butterfly-box-border-color-base: #D9D9D9;
@butterfly-box-shadow-base:0 2px 3px 0 rgba(0,112,204,0.06);
@butterfly-box-radius-base:100px;
@butterfly-line-color-base: #BFBFBF;
// 主题背景色（画布背景色）
@butterfly-theme-background-color: @butterfly-theme-color-base;
// （各图形框）普通情况背景色
@butterfly-theme-color: fade(@butterfly-theme-color-base,80%);
// 主题色
@butterfly-primary-color: @butterfly-primary-color-base;
// 普通情况下字体颜色
@butterfly-normal-font-color: @butterfly-normal-font-color-base;
// 主题背景色上的字体颜色及icon颜色
@butterfly-overlay-font-color: @butterfly-overlay-font-color-base;
// 拖动时的背景色
@butterfly-box-move-background-color: fade(@butterfly-primary-color, 20%);
// 边框的样式
@butterfly-box-border: 1px solid @butterfly-box-border-color-base;
@butterfly-box-shadow: @butterfly-box-shadow-base;
@butterfly-box-radius: @butterfly-box-radius-base;
@butterfly-box-node-border-hover: 1px solid @butterfly-primary-color;
// 线条样式
@butterfly-line-color: @butterfly-line-color-base;
