import {Edge} from 'butterfly-dag';
import $ from 'jquery';

class RelationEdge extends Edge {
  draw = (obj) => {
    let path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
    path.setAttribute('class', 'butterflies-link');
    path.addEventListener('mouseover', () => {
      $(this.labelDom).addClass('xxxx');
    });
    return path;
  }
  redraw = (sourcePoint, targetPoint, options) => {
    // 重新计算线条path
    let path = this._calcPath();
    this.dom.setAttribute('d', path);
    if (this.labelDom) {
      this.redrawLabel();
    }
  }
  drawLabel() {
    return $('<span class="butterflies-label">乘火车</span>')[0];
  }
}

export default RelationEdge;

import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import {Canvas} from 'butterfly-dag';
import mockData from './data.js';
import RelationEdge from './edgerelation.js';

import './index.less';
import './newIconfont.css';
import 'butterfly-dag/dist/index.css';

class RelationalNetwork extends Component {
  constructor() {
    super();
    this.canvas = null;
  }
  componentDidMount() {
    // css里面的类名限制太死了
    let root = document.getElementById('dag-canvas');
    this.canvas = new Canvas({
      root: root,
      disLinkable: true, // 可删除连线
      linkable: false,    // 可连线
      draggable: true,   // 可拖动
      zoomable: true,    // 可放大
      moveable: true,    // 可平移
      theme: {
        edge: {
          shapeType: 'Straight',
          Class: RelationEdge
        },
        endpoint: {
          position: ['Top', 'Bottom']
        }
      }
    });
    this.canvas.setMinimap(true);
    this.canvas.draw(mockData);
  }
  render() {
    return (
      <div className='relationalNetwork-page'>
        <div className="flow-canvas" id="dag-canvas">
        </div>
      </div>
    );
  }
}

ReactDOM.render(<RelationalNetwork />, document.getElementById('root'));

